    message: 'Falha ao inserir o registro',
    status: 'error'
  };
}

return msg;

msg.statusCode = 400;
msg.headers = { 'Content-Type': 'application/json' };
msg.payload = {
    message: 'Campos obrigatórios sem preenchimento',
    status: 'error'
}
return msg;

const id = msg.req.params.id; 

if (!id) {
    msg.statusCode = 400;
    msg.payload = { status: 'error', message: 'ID não informado' };
    return msg;
}

msg.topic = 'DELETE FROM aluno WHERE id = ?';
msg.payload = [id];
return msg;

const result = msg.payload;

